  /**
   * Notes:
   * - exceptions: , , , , , , , , , 
   * - exceptions: , , , , , , , , , , , , , , , , , , , , , , , , , , 
   * - exceptions: , , , , , , , , , , , , , , , , , , , , , , , , 
   */

  // Pluralize
  this.pluralForms.regularForms.push([/^(.+)$/i, '$1']);

  // Singularize
  this.singularForms.regularForms.push([/^(.+)$/i, function(a, mask) {
    if (['', '', '', '', '', '', '', '', '',
      ''].indexOf(mask) >= 0)
